import { ComponentFixture, TestBed } from '@angular/core/testing';
import { InvestmentComponent } from './investment.component';
import { ReactiveFormsModule } from '@angular/forms';
import { HttpClientTestingModule } from '@angular/common/http/testing';

describe('InvestmentComponent', () => {
  let component: InvestmentComponent;
  let fixture: ComponentFixture<InvestmentComponent>;

  beforeEach(async () => {
    await TestBed.configureTestingModule({
      declarations: [ InvestmentComponent ],
      imports: [ ReactiveFormsModule, HttpClientTestingModule ]
    })
    .compileComponents();
  });

  beforeEach(() => {
    fixture = TestBed.createComponent(InvestmentComponent);
    component = fixture.componentInstance;
    fixture.detectChanges();
  });

  it('should create', () => {
    expect(component).toBeTruthy();
  });

  it('should have a form with initialValue and periodInMonths controls', () => {
    expect(component.form.contains('initialValue')).toBeTruthy();
    expect(component.form.contains('periodInMonths')).toBeTruthy();
  });

  it('should make the initialValue control required', () => {
      let control = component.form.get('initialValue');
      if (control) {
          control.setValue('');
          expect(control.valid).toBeFalsy();
      }
  });
});
